INSERT INTO `t_noti` (`id`, `notikind`, `noticontent`, `time`) VALUES (1, '赛事安排', '2024年创新创业大赛（原“互联网+”创新创业大赛）软件学院选拔赛报名截止时间为5月23日。请各位同学积极参与，有意向参赛的队伍加入学院QQ群（群号：375627295）并尽快提交相关材料', '2024.5.15');
INSERT INTO `t_noti` (`id`, `notikind`, `noticontent`, `time`) VALUES (2, '温馨提示', '开学以来，全国多省份发生出现强对流天气和持续性强降雨过程。尤其是广东、广西、福建等省份受灾严重。随着夏季来临，全国大部分省份进入汛期阶段。\r\n如有同学家庭有受灾情况（包括之后），请随时与我联系。 ', '2024.5.19');
INSERT INTO `t_noti` (`id`, `notikind`, `noticontent`, `time`) VALUES (3, '温馨提示', '公安处反馈，3月至4月间学生出现多起网络被骗案件，被骗金额从千元至上万元不等。派出所号召师生共筑网络安全防线，如发现有师生疑似被骗情况需联系派出所，请拨打南区新城派出所。\r\n请大家相互提醒！\r\n@全体成员 ', '2024.5.23');
INSERT INTO `t_noti` (`id`, `notikind`, `noticontent`, `time`) VALUES (4, '活动安排', '为更好的吸纳学生干部骨干，软件学院团委决定在2024年6月中上旬对团委、学生会、学生科学技术协会（以下简称科协）、大学生志愿者协会（以下简称志协）、学生自我管理委员会（以下简称自管会）新媒体中心、文化艺术中心（以下简称文艺中心）、学生发展指导委员会（以下简称学发委）主席团换届进行新一轮竞选。选拔面向2022级全体部长级成员、2022级年级会主席团成员。\r\n详见通知文件。', '2024.5.24');
